contracts/airUSDSwappers/UniswapV2Swappe.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.9;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "../interfaces/IUniswapV2Router.sol";
import "../interfaces/ISwapperImpl.sol";

contract UniswapV2Swapper is ISwapperImpl {
    using SafeERC20 for IERC20;

    address public uniswapV2Router;
    address public override tokenIn;
    address public override tokenOut;
    address[] public path;

    constructor(
        address _uniswapV2Router,
        address _tokenIn,
        address _tokenOut,
        address[] memory _path
    ) {
        require(_tokenIn != _tokenOut, "invalid token");
        require(
            _path[0] == _tokenIn && _path[_path.length - 1] == _tokenOut,
            "invalid path"
        );

        uniswapV2Router = _uniswapV2Router;
        tokenIn = _tokenIn;
        tokenOut = _tokenOut;
        path = _path;
    }

    function swap(uint256 _amountIn, address _to)
        external
        override
        returns (uint256 amountOut)
    {
        IERC20(tokenIn).safeTransferFrom(msg.sender, address(this), _amountIn);
        IERC20(tokenIn).safeApprove(uniswapV2Router, _amountIn);

        uint256[] memory amountsOut = IUniswapV2Router(uniswapV2Router)
            .swapExactTokensForTokens(_amountIn, 0, path, _to, block.timestamp);

        return amountsOut[amountsOut.length - 1];
    }
}

contracts/interfaces/IUniswapV2Router.so.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.9;

interface IUniswapV2Router {
    function factory() external pure returns (address);

    // solhint-disable-next-line func-name-mixedcase
    function WETH() external view returns (address);

    function getAmountsIn(uint256 amountOut, address[] memory path)
        external
        view
        returns (uint256[] memory amounts);

    function getAmountsOut(uint256 amountIn, address[] memory path)
        external
        view
        returns (uint256[] memory amounts);

    function swapExactTokensForTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapTokensForExactTokens(
        uint256 amountOut,
        uint256 amountInMax,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function addLiquidity(
        address tokenA,
        address tokenB,
        uint256 amountADesired,
        uint256 amountBDesired,
        uint256 amountAMin,
        uint256 amountBMin,
        address to,
        uint256 deadline
    )
        external
        returns (
            uint256 amountA,
            uint256 amountB,
            uint256 liquidity
        );

    function removeLiquidity(
        address tokenA,
        address tokenB,
        uint256 liquidity,
        uint256 amountAMin,
        uint256 amountBMin,
        address to,
        uint256 deadline
    ) external returns (uint256 amountA, uint256 amountB);
}

contracts/airUSDSwappers/StethSwapper.so.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.9;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "../interfaces/IUniswapV2Router.sol";
import "../interfaces/ISwapperImpl.sol";
import "../interfaces/ICurvePool.sol";

contract StethAirUSDSwapper is ISwapperImpl {
    using SafeERC20 for IERC20;

    address public uniswapV2Router;
    address public airUSD;
    address public constant weth = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;
    address public constant stETH = 0xae7ab96520DE3A18E5e111B5EaAb095312D7fE84;
    address public constant stETHCurve =
        0x828b154032950C8ff7CF8085D841723Db2696056;

    constructor(address _uniswapV2Router, address _airUSD) {
        uniswapV2Router = _uniswapV2Router;
        airUSD = _airUSD;
    }

    function tokenIn() external pure override returns (address) {
        return stETH;
    }

    function tokenOut() external view override returns (address) {
        return airUSD;
    }

    function swap(uint256 _amountIn, address _to)
        external
        override
        returns (uint256 amountOut)
    {
        IERC20(stETH).safeTransferFrom(msg.sender, address(this), _amountIn);
        IERC20(stETH).safeApprove(uniswapV2Router, _amountIn);

        // considering stETH is rebase token
        _amountIn = IERC20(stETH).balanceOf(address(this));

        // swap steth -> weth
        ICurvePool(stETHCurve).exchange(1, 0, _amountIn, 0);

        // swap weth -> airUSD
        uint256 wethAmount = IERC20(weth).balanceOf(address(this));
        IERC20(weth).approve(uniswapV2Router, wethAmount);
        address[] memory path = new address[](2);
        path[0] = weth;
        path[1] = airUSD;
        uint256[] memory amountsOut = IUniswapV2Router(uniswapV2Router)
            .swapExactTokensForTokens(
                wethAmount,
                0,
                path,
                _to,
                block.timestamp
            );

        return amountsOut[amountsOut.length - 1];
    }
}

